import React, { useState } from "react";
import './LoginForm.css';

const LoginForm = (props) => {

	const [name,setUsername] = useState('');
	const [password,setPassword] = useState('');
	const [errorMessage, setErrorMessage] = useState("");
	const updateName = (event) => {
		const name = event.target.value;
		setUsername({ ...name, name })
	}

	const updatePassword = (event) => {
		const password = event.target.value;
		setPassword({ ...password, password })
	}

	const handleSubmit = (event) => {
		event.preventDefault();
        setErrorMessage("");
        if (name.trim()  && password.trim()) {

			
		props.onSubmit({
			login: name,
			password: password,
		});
        //  props.onAddNewTodo(newTodo, dueDate);
         
        } else {
          setErrorMessage(" User and password required. ");
        }
   
      }

	// const handleSubmit22 = (event) =>{
	// 	event.preventDefault();

	// 	props.onSubmit({
	// 		login: name,
	// 		password: password,
	// 	});
	// }

	return (
		<form className="form">
			<h1>Login</h1>
			<label htmlFor="name">Name</label>
			<input type="text" id="name" onChange={updateName} />
			<label htmlFor="password">Password</label>
			<input type="password" id="password"  onChange={updatePassword}/>
			<button type="submit" onClick={handleSubmit}>Continue</button>
			{errorMessage && (
            <p>{errorMessage}</p>
          )}
		</form>
	)
}

export default LoginForm;
